mapper.select:
select product_id,product_name,user_name, price, description,image,ranking,brand_name,status,update_time
from product
where status = '1'  
order by ranking desc 

	limit #{firstResult}
	, #{maxResult}

select product_id,product_name,user_name, price, description,image,ranking,brand_name,status,update_time
from product 
where status = '1'  

	limit #{firstResult}
	, #{maxResult}

select count(1)
from product 
where status = '1'
and brand_name = #{brand_name}
and product_name like #{product_name}

select product_id,product_name,user_name, price, description,image,ranking,brand_name,status,update_time
from product 
where status = '1'  
and brand_name = #{brand_name}
and product_name like #{product_name} 

	limit #{firstResult}
	, #{maxResult}

select product_id,product_name,user_name, price, description,image,ranking,brand_name,status,update_time
from product 
where status = '1'  
and brand_name = #{brand_name}  

	limit #{firstResult}
	, #{maxResult}

select product_id,product_name,user_name, price, description,image,ranking,brand_name,status,update_time
from product 
where status = '1'  
and (brand_name like #{brand_name} or product_name like #{product_name}) 

	limit #{firstResult}
	, #{maxResult}

select product_id,product_name,user_name, price, description,image,ranking,brand_name,status,update_time
from product 
where status = '1'  
and user_name = #{user_name} 

	limit #{firstResult}
	, #{maxResult}

select count(1)
from reviews 
where product_id = #{product_id}

select user_name,product_id,ranking,review
from reviews 
where product_id = #{product_id}  

	limit #{firstResult}
	, #{maxResult}

mapper.update:
update product set
product_name = #{product_name}, price = #{price}, description = #{description},image = #{image},
brand_name = #{brand_name},update_time = #{update_time} 
where product_id = #{product_id}

